tape( 'the plugin handles empty files', opts, function test( t ) {
	var fpath;
	var file;

	fpath = join( __dirname, 'fixtures', 'empty.md.txt' );
	file = readSync( fpath, 'utf8' );

	remark().use( run ).process( file, done );

	function done( error ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.pass( 'does not error' );
		}
		t.end();
	}
});

