/* eslint-disable no-console, stdlib/no-internal-require */

'use strict';

// MODULES //

var join = require( 'path' ).join;
var writeFileSync = require( '@stdlib/fs/write-file' ).sync;
var ls = require( '@stdlib/_tools/pkgs/names' ).sync;
var namespaceDeps = require( '@stdlib/_tools/pkgs/namespace-deps' );
var standalonePackage = require( '@stdlib/_tools/pkgs/namespace-deps/lib/standalone_package.js' ); // FIXME: avoid requiring package internals
var CompactAdjacencyMatrix = require( '@stdlib/utils/compact-adjacency-matrix' );
var indexOf = require( '@stdlib/utils/index-of' );
var contains = require( '@stdlib/assert/contains' );


// VARIABLES //

var standalonePkgs;
var sorted;
var pkgs;
var deps;
var bool;
var mat;
var idx;
var N;
var i;
var j;


// MAIN //

pkgs = ls();
N = pkgs.length;
console.log( 'Number of stdlib packages: %d', pkgs.length );

standalonePkgs = [];
for ( i = 0; i < N; i++ ) {
	standalonePkgs.push( standalonePackage( pkgs[ i ] ) );
}
mat = new CompactAdjacencyMatrix( N );

for ( i = 0; i < N; i++ ) {
	// console.log( 'Processing package: %s (index: %d)', pkgs[ i ], i );
	if (
		pkgs[ i ] === '@stdlib/process/cwd' ||
		pkgs[ i ] === '@stdlib/process/node-version'
	) {
		continue;
	}
	try {
		deps = namespaceDeps( pkgs[ i ], {
			'dev': false,
			'level': 999
		});
	} catch ( err ) {
		console.error( 'Encountered an error: %s', err.message );
		continue;
	}
	for ( j = 0; j < deps.length; j++ ) {
		idx = indexOf( standalonePkgs, deps[ j ] );
		if ( idx >= 0 && !contains( deps[ j ], '_tools' ) ) {
			mat.addEdge( idx, i );
		}
	}
}

for ( i = 0; i < N; i++ ) {
	for ( j = 0; j < N; j++ ) {
		bool = mat.hasEdge( i, j );
		if ( bool && bool === mat.hasEdge( j, i ) ) {
			console.log( 'Encountered cycle: (%s, %s)', pkgs[ i ], pkgs[ j ] );
		}
	}
}

console.log( 'Starting topological search...' );
sorted = mat.toposort();

if ( sorted ) {
	for ( i = 0; i < sorted.length; i++ ) {
		sorted[ i ] = pkgs[ sorted[ i ] ];
	}
	console.log( sorted );

	// writeFileSync( join( __dirname, './ordered.json' ), JSON.stringify( sorted, null, '  ' ).concat( '\n' ) );
} else {
	console.error( 'Unable to determine a topological ordering.' );
}
