'use strict';

var resolve = require( 'path' ).resolve;
var lunr = require( 'lunr' );
var create = require( './../lib' );

create({
	'dir': resolve( __dirname, './../../../' )
}, onIndex );

function onIndex( error, idx ) {
	var store;
	if ( error ) {
		throw error;
	}
	store = lunr.Index.load( idx );
	console.log( store.search( 'encrypt' ) );
}
