'use strict';

/**
* Incrementally calculate logical lines of code (LLOC).
*
* @module @stdlib/_tools/static-analysis/js/incr/lloc
*
* @example
* var analyze = require( '@stdlib/_tools/static-analysis/js/incr/lloc' );
*
* // Create an accumulator:
* var acc = analyze();
*
* // Feed the accumulator JavaScript programs...
* var prog = '"use strict";\n\nconsole.log( "beep" );\n';
* var out = acc( prog );
* // returns {...}
*
* prog = '"use strict";\n\nvar x = 5;\n';
* out = acc( prog );
* // returns {...}
*
* prog = '"use strict";\n\nmodule.exports = "beep";\n';
* out = acc( prog );
* // returns {...}
*/

// MODULES //

var analyze = require( './main.js' );


// EXPORTS //

module.exports = analyze;
