#!/usr/bin/env node
'use strict';

// MODULES //

var fields = require( './../lib/fields.json' );


// VARIABLES //

var INDENTATION = '    ';


// MAIN //

/**
* Prints a Markdown formatted list listing program metrics for inclusion in the README.
*
* @private
*/
function main() {
	var line;
	var i;

	for ( i = 0; i < fields.length; i++ ) {
		// List indentation:
		line = INDENTATION;

		// List marker:
		line += '-   ';

		// Metric name (in bold):
		line += '**' + fields[ i ][ 0 ] + '**';

		// Definition separator:
		line += ':';

		// Definition:
		line += ' ' + fields[ i ][ 2 ] + '.';

		console.log( line ); // eslint-disable-line no-console
	}
}

main();
