'use strict';

// MODULES //

var tape = require( 'tape' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isFunction = require( '@stdlib/assert/is-function' );
var analyze = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof analyze, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously calculate LLOC', function test( t ) {
	t.strictEqual( hasOwnProp( analyze, 'sync' ), true, 'has property' );
	t.strictEqual( isFunction( analyze.sync ), true, 'has method' );
	t.end();
});
