'use strict';

// MAIN //

/**
* Extracts literal data.
*
* @private
* @param {Node} node - literal AST node
* @returns {ObjectArray} literal data
*/
function literalinfo( node ) {
	var out = {};
	if ( node.value === null ) {
		out.type = 'null';
	} else if ( node.regex ) {
		out.type = 'regexp';
	} else {
		out.type = typeof node.value;
	}
	return out;
}


// EXPORTS //

module.exports = literalinfo;
