'use strict';

// MODULES //

var isObject = require( '@stdlib/assert/is-plain-object' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;


// MAIN //

/**
* Validates function options.
*
* @private
* @param {Object} opts - destination object
* @param {Options} options - function options
* @param {boolean} [options.cumulative] - boolean indicating whether to perform a cumulative analysis
* @returns {(Error|null)} null or an error object
*
* @example
* var opts = {};
* var options = {
*     'cumulative': true
* };
* var err = validate( opts, options );
* if ( err ) {
*     throw err;
* }
*/
function validate( opts, options ) {
	if ( !isObject( options ) ) {
		return new TypeError( 'invalid input argument. Options must be an object. Value: `' + options + '`.' );
	}
	if ( hasOwnProp( options, 'cumulative' ) ) {
		opts.cumulative = options.cumulative;
		if ( !isBoolean( opts.cumulative ) ) {
			return new TypeError( 'invalid option. `cumulative` option must be a boolean. Option: `' + opts.cumulative + '`.' );
		}
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
