'use strict';

/**
* Calculate source lines of code (SLOC) on a file glob.
*
* @module @stdlib/_tools/static-analysis/js/sloc-glob
*
* @example
* var analyze = require( '@stdlib/_tools/static-analysis/js/sloc-glob' );
*
* var opts = {
*     'dir': __dirname
* };
*
* analyze( opts, clbk );
*
* function clbk( error, results ) {
*     if ( error ) {
*         throw error;
*     }
*     if ( results ) {
*         console.log( JSON.stringify( results ) );
*     }
* }
*
* @example
* var analyze = require( '@stdlib/_tools/static-analysis/js/sloc-glob' ).sync;
*
* var opts = {
*     'dir': __dirname
* };
*
* var results = analyze( opts );
* if ( results instanceof Error ) {
*     throw results;
* }
* console.log( JSON.stringify( results ) );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var analyze = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( analyze, 'sync', sync );


// EXPORTS //

module.exports = analyze;
