'use strict';

// MODULES //

var programSummary = require( '@stdlib/_tools/static-analysis/js/program-summary' );


// MAIN //

/**
* Calculates the source lines of code (SLOC) in a JavaScript program.
*
* @param {(string|Buffer)} input - input program
* @throws {TypeError} must provide a string or Buffer
* @returns {NonNegativeInteger} source lines of code (SLOC)
*
* @example
* var prog = 'var x = 5;\n';
*
* var n = sloc( prog );
* // returns 1
*/
function sloc( input ) {
	return programSummary( input ).sloc;
} // end FUNCTION sloc()


// EXPORTS //

module.exports = sloc;
