'use strict';

// MODULES //

var tape = require( 'tape' );
var string2buffer = require( '@stdlib/buffer/from-string' );
var sloc = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof sloc, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a string or a Buffer', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			sloc( value );
		};
	}
});

tape( 'the function returns the source lines of code (SLOC) (string)', function test( t ) {
	var n = sloc( 'var beep = "boop";\n' );
	t.strictEqual( n, 1, 'returns expected value' );
	t.end();
});

tape( 'the function returns the source lines of code (SLOC) (Buffer)', function test( t ) {
	var n = sloc( string2buffer( 'var beep = "boop";\nz = foo( y );\n' ) );
	t.strictEqual( n, 2, 'returns expected value' );
	t.end();
});
