'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var hasArrayBufferSupport = require( '@stdlib/utils/detect-arraybuffer-support' );
var pow = require( '@stdlib/math/base/special/pow' );
var isArrayBuffer = require( '@stdlib/assert/is-arraybuffer' );
var pkg = require( './../package.json' ).name;
var ctor = require( './../lib' );


// VARIABLES //

var opts = {
	'skip': !hasArrayBufferSupport() // TODO: remove once ArrayBuffer polyfill is written
};


// FUNCTIONS //

/**
* Creates a benchmark function.
*
* @private
* @param {PositiveInteger} len - array buffer length
* @returns {Function} benchmark function
*/
function createBenchmark( len ) {
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var buf;
		var i;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			buf = new ctor( len );
			if ( buf.byteLength !== len ) {
				b.fail( 'unexpected length' );
			}
		}
		b.toc();
		if ( !isArrayBuffer( buf ) ) {
			b.fail( 'should return an ArrayBuffer' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var len;
	var min;
	var max;
	var f;
	var i;

	min = 1; // 10^min
	max = 6; // 10^max

	for ( i = min; i <= max; i++ ) {
		len = pow( 10, i );
		f = createBenchmark( len );
		bench( pkg+':len='+len, opts, f );
	}
}

main();
