#!/usr/bin/env python
"""Benchmark numpy.ndarray.astype."""

from __future__ import print_function
import timeit

NAME = "convert"
REPEATS = 3
ITERATIONS = 1000000
COUNT = [0]  # use a list to allow modification within nested scopes


def print_version():
    """Print the TAP version."""
    print("TAP version 13")


def print_summary(total, passing):
    """Print the benchmark summary.

    # Arguments

    * `total`: total number of tests
    * `passing`: number of passing tests

    """
    print("#")
    print("1.." + str(total))  # TAP plan
    print("# total " + str(total))
    print("# pass  " + str(passing))
    print("#")
    print("# ok")


def print_results(iterations, elapsed):
    """Print benchmark results.

    # Arguments

    * `iterations`: number of iterations
    * `elapsed`: elapsed time (in seconds)

    # Examples

    ``` python
    python> print_results(100000, 0.131009101868)
    ```
    """
    rate = iterations / elapsed

    print("  ---")
    print("  iterations: " + str(iterations))
    print("  elapsed: " + str(elapsed))
    print("  rate: " + str(rate))
    print("  ...")


def benchmark(name, setup, stmt, iterations):
    """Run the benchmark and print benchmark results.

    # Arguments

    * `name`: benchmark name (suffix)
    * `setup`: benchmark setup
    * `stmt`: statement to benchmark
    * `iterations`: number of iterations

    # Examples

    ``` python
    python> benchmark("::random", "from random import random;", "y = random()", 1000000)
    ```
    """
    t = timeit.Timer(stmt, setup=setup)

    i = 0
    while i < REPEATS:
        print("# python::numpy::" + NAME + name)
        COUNT[0] += 1
        elapsed = t.timeit(number=iterations)
        print_results(iterations, elapsed)
        print("ok " + str(COUNT[0]) + " benchmark finished")
        i += 1


def main():
    """Run the benchmarks."""
    print_version()

    dtypes = [
        "float64",
        "float32",
        "int32",
        "int16",
        "int8",
        "uint32",
        "uint16",
        "uint8"
    ]
    iters = ITERATIONS
    n = 1
    while n < 1e7:
        n *= 10
        for i in range(0, len(dtypes)):
            dtype = dtypes[i]
            name = ":len="+str(n)+",dtype="+dtype
            setup = "import numpy as np;"
            setup += "arr = np.ones([1,"+str(n)+"], dtype='"+dtype+"');"
            stmt = "y = arr.astype('"+dtype+"')"
            benchmark(name, setup, stmt, iters)
        iters /= 4

    print_summary(COUNT[0], COUNT[0])


if __name__ == "__main__":
    main()
