'use strict';

var dtypes = require( '@stdlib/array/dtypes' );
var ctors = require( '@stdlib/array/ctors' );
var dtype = require( './../lib' );

var DTYPES;
var ctor;
var arr;
var len;
var dt;
var i;

// Get a list of supported array data types:
DTYPES = dtypes();

// Array length:
len = 10;

// For each supported data type, create an array and confirm its data type...
for ( i = 0; i < DTYPES.length; i++ ) {
	ctor = ctors( DTYPES[ i ] );
	arr = new ctor( len ); // eslint-disable-line no-new-cap
	dt = dtype( arr );
	console.log( '%s == %s => %s', DTYPES[ i ], dt, DTYPES[ i ] === dt );
}
