/* eslint-disable object-curly-newline */
'use strict';

var deepHasOwn = require( './../lib' );

var bool;
var has;
var obj;

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = deepHasOwn( obj, 'a.b.c' );
console.log( bool );
// => true

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = deepHasOwn( obj, [ 'a', 'b', 'c' ] );
console.log( bool );
// => true

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = deepHasOwn( obj, 'a/b/c', {
	'sep': '/'
});
console.log( bool );
// => true

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = deepHasOwn( obj, 'a.b.c.d' );
console.log( bool );
// => false

obj = { 'a': [ { 'b': { 'c': 'd' } } ] };
bool = deepHasOwn( obj, [ 'a', '0', 'b', 'c', 'd' ] );
console.log( bool );
// => false

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = deepHasOwn( obj, 'a/b/c/d/e', {
	'sep': '/'
});
console.log( bool );
// => false

// Create a customized function:
has = deepHasOwn.factory( 'a_b_c', {
	'sep': '_'
});

obj = { 'a': { 'b': { 'c': 'd' } } };
bool = has( obj );
console.log( bool );
// => true

obj = { 'a': [ { 'b': { 'c': 'd' } } ] };
bool = has( obj );
console.log( bool );
// => false
