tape( 'the returned function returns `false` if provided an empty object (no options)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a.b.c' );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	has = factory( '' );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	t.end();
});

tape( 'the returned function returns `false` if provided an empty object (options)', function test( t ) {
	var bool;
	var opts;
	var has;

	opts = {
		'sep': '/'
	};

	has = factory( 'a/b/c', opts );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	has = factory( '', opts );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	t.end();
});

tape( 'the returned function returns `false` if provided an empty key path (no options)', function test( t ) {
	var bool;
	var obj;
	var has;

	has = factory( '' );
	obj = {
		'a': 'b'
	};
	bool = has( obj );
	t.strictEqual( bool, false, 'returns false' );

	has = factory( '' );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	t.end();
});

tape( 'the returned function returns `false` if provided an empty key path (options)', function test( t ) {
	var bool;
	var opts;
	var has;
	var obj;

	opts = {
		'sep': '/'
	};

	has = factory( '', opts );
	obj = {
		'a': 'b'
	};
	bool = has( obj );
	t.strictEqual( bool, false, 'returns false' );

	has = factory( '', opts );
	bool = has( {} );
	t.strictEqual( bool, false, 'returns false' );

	t.end();
});
