/* eslint-disable object-curly-newline */
'use strict';

// MODULES //

var tape = require( 'tape' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof factory, 'function', 'export is a function' );
	t.end();
});

tape( 'the function throws an error if provided a key path argument which is neither a string primitive or a key array', function test( t ) {
	var values;
	var i;

	values = [
		3,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws an error when provided '+values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if provided an options argument which is not an object', function test( t ) {
	var values;
	var i;

	values = [
		'3',
		3,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws an error when provided '+values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( 'a.b', value );
		};
	}
});

tape( 'the function throws an error if an invalid option', function test( t ) {
	var values;
	var i;

	values = [
		3,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws an error when provided '+values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( 'a.b', {
				'sep': value
			});
		};
	}
});

tape( 'the function returns a function (no options)', function test( t ) {
	var has = factory( 'a.b.c' );
	t.strictEqual( typeof has, 'function', 'returns a function' );
	t.end();
});

tape( 'the function returns a function (options)', function test( t ) {
	var has = factory( 'a/b/c', {
		'sep': '/'
	});

	t.strictEqual( typeof has, 'function', 'returns a function' );
	t.end();
});

tape( 'the function returns `false` if provided a `null` or `non-object` (no options)', function test( t ) {
	var values;
	var has;
	var i;

	values = [
		'3',
		3,
		NaN,
		true,
		false,
		null,
		void 0,
		function noop() {}
	];

	has = factory( 'a.b.c' );

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( has( values[ i ] ), false, 'returns false when provided '+values[ i ] );
	}
	t.end();
});

tape( 'the function returns `false` if provided a `null` or `non-object` (options)', function test( t ) {
	var values;
	var has;
	var i;

	values = [
		'3',
		3,
		NaN,
		true,
		false,
		null,
		void 0,
		function noop() {}
	];

	has = factory( 'a_b_c', {
		'sep': '_'
	});

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( has( values[ i ] ), false, 'returns false when provided '+values[ i ] );
	}
	t.end();
});

tape( 'the returned function returns `true` if an object has a specified path (delimited string)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a.b.0.c' );
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the returned function returns `true` if an object has a specified path (key array)', function test( t ) {
	var bool;
	var has;

	has = factory( [ 'a', 'b', '0', 'c' ] );
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the returned function returns `true` if an object has a specified path (non-default delimiter)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a/b/0/c', {
		'sep': '/'
	});
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the returned function returns `false` if an object does not have a specified path (delimited string)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a.b.c.0.d' );
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, false, 'returns false' );
	t.end();
});

tape( 'the returned function returns `false` if an object does not have a specified path (key array)', function test( t ) {
	var bool;
	var has;

	has = factory( [ 'a', 'b', 'c', '0', 'd' ] );
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, false, 'returns false' );
	t.end();
});

tape( 'the returned function returns `false` if an object does not have a specified path (non-default delimiter)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a/b/c/0/d', {
		'sep': '/'
	});
	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );

	t.strictEqual( bool, false, 'returns false' );
	t.end();
});
