'use strict';

// MODULES //

var tape = require( 'tape' );
var isASCII = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isASCII, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a string containing all ASCII characters', function test( t ) {
	t.strictEqual( isASCII( 'abcdef' ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a string containing all ASCII characters', function test( t ) {
	var values;
	var i;

	values = [
		'',
		'Ãˆ',
		new String( 'abcdef' ), // eslint-disable-line no-new-wrappers
		5,
		null,
		NaN,
		undefined,
		true,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isASCII( values[i] ), false, 'returns false when provided ' + values[i] );
	}
	t.end();
});
