'use strict';

// MODULES //

var tape = require( 'tape' );
var isBoolean = require( './../lib/generic.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isBoolean, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a boolean', function test( t ) {
	t.equal( isBoolean( false ), true, 'returns true' );
	t.equal( isBoolean( new Boolean( false ) ), true, 'returns true' );
	t.equal( isBoolean( true ), true, 'returns true' );
	t.equal( isBoolean( new Boolean( true ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a boolean', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		null,
		5,
		NaN,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isBoolean( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
