'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var IS_DARWIN = require( './../lib' );


// TESTS //

tape( 'main export is a boolean', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof IS_DARWIN, 'boolean', 'main export is a boolean' );
	t.end();
});

tape( 'the main export is `true` if the current process is running on Darwin', function test( t ) {
	var IS_DARWIN;

	IS_DARWIN = proxyquire( './../lib', {
		'@stdlib/utils/platform': 'darwin'
	});
	t.equal( IS_DARWIN, true, 'is true' );
	t.end();
});

tape( 'the main export is `false` if the current process is not running on Darwin', function test( t ) {
	var IS_DARWIN;

	IS_DARWIN = proxyquire( './../lib', {
		'@stdlib/utils/platform': 'linux'
	});
	t.equal( IS_DARWIN, false, 'is false' );
	t.end();
});
