/* eslint-disable no-new-wrappers */
'use strict';

var isEmptyString = require( './../lib' );

console.log( isEmptyString( '' ) );
// => true

console.log( isEmptyString( new String( '' ) ) );
// => true

console.log( isEmptyString( 'beep' ) );
// => false

console.log( isEmptyString( 0 ) );
// => false

console.log( isEmptyString( null ) );
// => false

console.log( isEmptyString( void 0 ) );
// => false

console.log( isEmptyString( {} ) );
// => false

console.log( isEmptyString( [] ) );
// => false

function empty() {
	// empty function...
}

console.log( isEmptyString( empty ) );
// => false
