'use strict';

// MODULES //

var tape = require( 'tape' );
var isEmptyString = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isEmptyString, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for an empty primitive string', function test( t ) {
	t.strictEqual( typeof isEmptyString.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for an empty string object', function test( t ) {
	t.strictEqual( typeof isEmptyString.isObject, 'function', 'export is a function' );
	t.end();
});
