/* eslint-disable no-new-wrappers */
'use strict';

// MODULES //

var tape = require( 'tape' );
var isEmptyString = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isEmptyString, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided an empty primitive string', function test( t ) {
	t.strictEqual( isEmptyString( '' ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a string object', function test( t ) {
	t.strictEqual( isEmptyString( new String( '' ) ), false, 'returns false' );
	t.end();
});

tape( 'the function returns `false` if not provided an empty string', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		new String( '5' ),
		5,
		NaN,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isEmptyString( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
