'use strict';

// MODULES //

var tape = require( 'tape' );
var isEven = require( './../lib/generic.js' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isEven, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided an even number', function test( t ) {
	t.equal( isEven( 4 ), true, 'returns true' );
	t.equal( isEven( new Number( 4 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided an even number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		3.0,
		3.14,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isEven( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
