'use strict';

// MODULES //

var tape = require( 'tape' );
var isEven = require( './../lib/object.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isEven, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided an even number object', function test( t ) {
	t.equal( isEven( new Number( 4 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a primitive number, even if the number is even', function test( t ) {
	t.equal( isEven( 4 ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided an even number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		null,
		true,
		undefined,
		[],
		{},
		new Date(),
		/./,
		new RegExp( '.' ),
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isEven( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
