'use strict';

var isFalsyArray = require( './../lib' );

var bool = isFalsyArray( [ null, '', NaN, 0, void 0, false ] );
console.log( bool );
// => true

bool = isFalsyArray( [ [], {}, true, 'a', 5, -5 ] );
console.log( bool );
// => false

bool = isFalsyArray( [] );
console.log( bool );
// => false

bool = isFalsyArray( false );
console.log( bool );
// => false

bool = isFalsyArray( null );
console.log( bool );
// => false
