'use strict';

/**
* Test if a value is falsy.
*
* @module @stdlib/assert/is-falsy
*
* @example
* var isFalsy = require( '@stdlib/assert/is-falsy' );
*
* var bool = isFalsy( false );
* // returns true
*
* bool = isFalsy( null );
* // returns true
*
* bool = isFalsy( '' );
* // returns true
*
* bool = isFalsy( 0 );
* // returns true
*
* bool = isFalsy( void 0 );
* // returns true
*
* bool = isFalsy( NaN );
* // returns true
*
* bool = isFalsy( [] );
* // returns false
*/

// MODULES //

var isFalsy = require( './is_falsy.js' );


// EXPORTS //

module.exports = isFalsy;
