/* eslint-disable no-new-wrappers, stdlib/no-redeclare */
'use strict';

var isFinite = require( './../lib' );

console.log( isFinite( -5.0 ) );
// => true

console.log( isFinite( 0.0 ) );
// => true

console.log( isFinite( new Number( 5.0 ) ) );
// => true

console.log( isFinite( 5.256 ) );
// => true

console.log( isFinite( 1.0/0.0 ) );
// => false

console.log( isFinite( -1.0/0.0 ) );
// => false

console.log( isFinite( '5' ) );
// => false

console.log( isFinite( null ) );
// => false
