/* eslint-disable no-redeclare */
'use strict';

// MODULES //

var tape = require( 'tape' );
var isFinite = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isFinite, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a finite value', function test( t ) {
	t.strictEqual( typeof isFinite.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a finite value', function test( t ) {
	t.strictEqual( typeof isFinite.isObject, 'function', 'export is a function' );
	t.end();
});
