'use strict';

// MODULES //

var tape = require( 'tape' );
var isFunction = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isFunction, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a function', function test( t ) {
	function beep() {
		return 'boop';
	}
	t.strictEqual( isFunction( beep ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a function', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		undefined,
		true,
		false,
		null,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isFunction( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
