/* eslint-disable no-new-wrappers */
'use strict';

var isInfinite = require( './../lib' );

console.log( isInfinite( 1.0/0.0 ) );
// => true

console.log( isInfinite( -1.0/0.0 ) );
// => true

console.log( isInfinite( new Number( 1.0/0.0 ) ) );
// => true

console.log( isInfinite( -5.0 ) );
// => false

console.log( isInfinite( 0.0 ) );
// => false

console.log( isInfinite( 5.256 ) );
// => false

console.log( isInfinite( '1.0/0.0' ) );
// => false

console.log( isInfinite( null ) );
// => false
