/* eslint-disable no-new-wrappers */
'use strict';

// MODULES //

var tape = require( 'tape' );
var isInfinite = require( './../lib/generic.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isInfinite, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number having an infinite value', function test( t ) {
	t.strictEqual( isInfinite( 1.0/0.0 ), true, 'returns true' );
	t.strictEqual( isInfinite( new Number( 1.0/0.0 ) ), true, 'returns true' );
	t.strictEqual( isInfinite( -1.0/0.0 ), true, 'returns true' );
	t.strictEqual( isInfinite( new Number( -1.0/0.0 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a number having an infinite value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		1.0,
		-1.0,
		NaN,
		null,
		true,
		false,
		void 0,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isInfinite( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
