'use strict';

// MODULES //

var tape = require( 'tape' );
var isInfinite = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isInfinite, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having an infinite value', function test( t ) {
	t.strictEqual( typeof isInfinite.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having an infinite value', function test( t ) {
	t.strictEqual( typeof isInfinite.isObject, 'function', 'export is a function' );
	t.end();
});
