'use strict';

var Int8Array = require( '@stdlib/array/int8' );
var isMatrixLike = require( './../lib' );

var mat = {
	'data': new Int8Array( 10 ),
	'shape': [ 5, 2 ],
	'offset': 0,
	'strides': [ 2, 1 ],
	'dtype': 'int8',
	'length': 10
};
console.log( isMatrixLike( mat ) );
// => true

console.log( isMatrixLike( [] ) );
// => false

console.log( isMatrixLike( {} ) );
// => false

console.log( isMatrixLike( null ) );
// => false
