'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( './../lib/generic.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isnan, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided `NaN`', function test( t ) {
	t.equal( isnan( NaN ), true, 'returns true' );
	t.equal( isnan( new Number( NaN ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided `NaN`', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		3.14,
		-1.0,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isnan( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
