'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isnan, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a `NaN` primitive number', function test( t ) {
	t.equal( typeof isnan.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a value of `NaN`', function test( t ) {
	t.equal( typeof isnan.isObject, 'function', 'export is a function' );
	t.end();
});
