'use strict';

// MODULES //

var tape = require( 'tape' );
var isNegativeNumber = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNegativeNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a primitive number having a negative value', function test( t ) {
	t.equal( isNegativeNumber( -3.0 ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a number object, even if the number has a negative value', function test( t ) {
	t.equal( isNegativeNumber( new Number( -5.0 ) ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a negative number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		0.0,
		new Number( 2.0 ),
		3.14,
		null,
		true,
		undefined,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNegativeNumber( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
