'use strict';

// MODULES //

var tape = require( 'tape' );
var isNodeBuiltin = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isNodeBuiltin, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a string primitive matching a Node.js built-in module name', function test( t ) {
	t.strictEqual( isNodeBuiltin( 'fs' ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a string primitive matching a Node.js built-in module name', function test( t ) {
	var values;
	var i;

	values = [
		'fs-extra',
		'd3',
		'@stdlib',
		new String( 'fs' ), // eslint-disable-line no-new-wrappers
		5,
		null,
		NaN,
		undefined,
		true,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isNodeBuiltin( values[i] ), false, 'returns false when provided ' + values[i] );
	}
	t.end();
});
