'use strict';

// MODULES //

var IS_NODE = require( '@stdlib/assert/is-node' );
var mod = require( './mod.js' );


// VARIABLES //

// The module id for the builtin REPL was `repl` in Node versions <4 and then changed to `<repl>` for Node versions >=4 (see https://github.com/nodejs/node/commit/ee72ee753118f2576bfd1ccf58fb2ff651e8bfb0#diff-b13d72249263845d8e8341db0426f9d3R527).
var RE_MODULE_ID = /^repl$|^<repl>$/;


// MAIN //

/**
* Returns a boolean indicating if a module is running within a Node.js REPL environment.
*
* ## Notes
*
* * False positives are possible due to the existence of a userland package having the same `id` (see [repl]{@link https://www.npmjs.com/package/repl}).
*
*
* @returns {boolean} boolean indicating if running within a Node.js REPL environment
*
* @example
* var bool = isNodeREPL();
* // returns <boolean>
*/
function isNodeREPL() {
	var m;
	if ( !IS_NODE ) {
		return false;
	}
	// Walk up the module dependency tree to find a `repl` ancestor...
	m = mod;
	while ( m ) {
		if ( RE_MODULE_ID.test( m.id ) ) {
			return true;
		}
		m = m.parent;
	}
	// Unable to find a `repl` ancestor:
	return false;
} // end FUNCTION isNodeREPL()


// EXPORTS //

module.exports = isNodeREPL;
