'use strict';

// MODULES //

var tape = require( 'tape' );
var isNodeREPL = require( './../lib/browser.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isNodeREPL, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function always returns `false`', function test( t ) {
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.strictEqual( isNodeREPL(), false, 'returns false' );
	t.end();
});
