'use strict';

// MODULES //

var tape = require( 'tape' );
var isNodeREPL = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isNodeREPL, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a boolean', function test( t ) {
	t.strictEqual( typeof isNodeREPL(), 'boolean', 'returns a boolean' );
	t.end();
});
