'use strict';

// MODULES //

var tape = require( 'tape' );
var isNonNegativeNumber = require( './../lib/object.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isNonNegativeNumber, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number object having a nonnegative number value', function test( t ) {
	t.equal( isNonNegativeNumber( new Number( 5.0 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a primitive number, even if the number is a nonnegative number value', function test( t ) {
	t.equal( isNonNegativeNumber( 3.0 ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a nonnegative number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		new Number( -2.0 ),
		-3.14,
		null,
		true,
		undefined,
		[],
		{},
		new Date(),
		/./,
		new RegExp( '.' ),
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isNonNegativeNumber( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
