/* eslint-disable no-new-wrappers */
'use strict';

var isNonPositiveNumber = require( './../lib' );

console.log( isNonPositiveNumber( -5.0 ) );
// => true

console.log( isNonPositiveNumber( new Number( -5.0 ) ) );
// => true

console.log( isNonPositiveNumber( 0.0 ) );
// => true

console.log( isNonPositiveNumber( -3.14 ) );
// => true

console.log( isNonPositiveNumber( 5.0 ) );
// => false

console.log( isNonPositiveNumber( '-5' ) );
// => false

console.log( isNonPositiveNumber( null ) );
// => false
