'use strict';

var Int8Array = require( '@stdlib/array/int8' );
var Uint8Array = require( '@stdlib/array/uint8' );
var Uint8ClampedArray = require( '@stdlib/array/uint8c' );
var Int16Array = require( '@stdlib/array/int16' );
var Uint16Array = require( '@stdlib/array/uint16' );
var Int32Array = require( '@stdlib/array/int32' );
var Uint32Array = require( '@stdlib/array/uint32' );
var Float32Array = require( '@stdlib/array/float32' );
var Float64Array = require( '@stdlib/array/float64' );
var isNumericArray = require( './../lib' );

var arr = new Int8Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint8Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint8ClampedArray( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Int16Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint16Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Int32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Uint32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Float32Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = new Float64Array( 10 );
console.log( isNumericArray( arr ) );
// => true

arr = [ 1, 2, 3 ];
console.log( isNumericArray( arr ) );
// => true

console.log( isNumericArray( [] ) );
// => false

console.log( isNumericArray( {} ) );
// => false

console.log( isNumericArray( null ) );
// => false

console.log( isNumericArray( new Buffer( 10 ) ) );
// => false
