'use strict';

// MODULES //

var tape = require( 'tape' );
var isOdd = require( './../lib/primitive.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isOdd, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a primitive odd number', function test( t ) {
	t.equal( isOdd( -5.0 ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a number object, even if the number is odd', function test( t ) {
	t.equal( isOdd( new Number( 5.0 ) ), false, 'returns false' );
	t.end();
});

tape( 'the function returns `false` if not provided an odd number', function test( t ) {
	var values;
	var i;

	values = [
		'4',
		4.0,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {},
		Number.POSITIVE_INFINITY,
		Number.NEGATIVE_INFINITY,
		NaN
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isOdd( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
