'use strict';

// MODULES //

var tape = require( 'tape' );
var isPlainObjectArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isPlainObjectArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function tests for an array-like object containing only plain objects', function test( t ) {
	var arr;

	arr = [ { 'type': 'object' }, {}, {} ];
	t.strictEqual( isPlainObjectArray( arr ), true, 'returns true' );

	arr = [ { 'type': 'object' }, /.*/, {} ];
	t.strictEqual( isPlainObjectArray( arr ), false, 'returns false' );

	arr = [ 5.0, {}, new Date() ];
	t.strictEqual( isPlainObjectArray( arr ), false, 'returns false' );

	arr = {'length': 2, '0': {}, '1': {} };
	t.strictEqual( isPlainObjectArray( arr ), true, 'returns true' );

	arr = [];
	t.strictEqual( isPlainObjectArray( arr ), false, 'returns false when provided an empty array' );

	t.end();
});

