'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveInteger = require( './../lib/generic.js' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isPositiveInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number having a positive integer value', function test( t ) {
	t.equal( isPositiveInteger( 5.0 ), true, 'returns true' );
	t.equal( isPositiveInteger( new Number( 5.0 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a number having a positive integer value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		3.14,
		-1.0,
		0.0,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isPositiveInteger( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
