/* eslint-disable no-new-wrappers */
'use strict';

var isPositiveZero = require( './../lib' );

console.log( isPositiveZero( 0.0 ) );
// => true

console.log( isPositiveZero( new Number( 0.0 ) ) );
// => true

console.log( isPositiveZero( -3.14 ) );
// => false

console.log( isPositiveZero( -0.0 ) );
// => false

console.log( isPositiveZero( 5.0 ) );
// => false

console.log( isPositiveZero( '0' ) );
// => false

console.log( isPositiveZero( null ) );
// => false
