'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveZero = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isPositiveZero, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number equal to positive zero', function test( t ) {
	t.strictEqual( typeof isPositiveZero.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a value equal to positive zero', function test( t ) {
	t.strictEqual( typeof isPositiveZero.isObject, 'function', 'export is a function' );
	t.end();
});
