'use strict';

// MODULES //

var tape = require( 'tape' );
var isSafeInteger = require( './../lib/generic.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isSafeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number having a safe integer value', function test( t ) {
	t.strictEqual( isSafeInteger( 5 ), true, 'returns true' );
	t.strictEqual( isSafeInteger( -5 ), true, 'returns true' );
	t.strictEqual( isSafeInteger( new Number( 5 ) ), true, 'returns true' );
	t.strictEqual( isSafeInteger( new Number( -5 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a number having a safe integer value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		3.14,
		2.0e200,
		-2.0e200,
		null,
		true,
		undefined,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isSafeInteger( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
